from flask import render_template, url_for, flash, redirect, request, abort, jsonify
import json, requests
from website import app

images = {
          'Thunderstorm': "https://patch.com/img/cdn20/users/23195148/20190717/030412/styles/patch_image/public/thunderstorm___17150231562.jpg",
          'Drizzle': "https://cdn.pixabay.com/photo/2015/08/03/22/25/rain-874041_960_720.jpg",
          'Rain': "http://img.freepik.com/free-vector/girl-with-umbrella_1325-5.jpg?size=338&ext=jpg",
          'Snow': "data:image/jpeg;base64,",
          'Clear': "https://s-media-cache-ak0.pinimg.com/564x/cf/1e/c4/cf1ec4b0c96e59657a46867a91bb0d1e.jpg",
          'Clouds': "data:image/jpeg;base64,",
          'Mist': "data:image/jpeg;base64,"
          }

@app.route('/')
def home():
    vij = requests.get(
        "http://api.openweathermap.org/data/2.5/weather?q=Amsterdam&appid=e3b51d955ccf5b1668a7ed5f68581870"
      )

    gnt = requests.get(
        "http://api.openweathermap.org/data/2.5/weather?q=Netherlands&appid=e3b51d955ccf5b1668a7ed5f68581870"
      )
    temp=json.loads(gnt.text)
    print(temp['weather'][0]['main'])
    return render_template('layout.html', vij=json.loads(vij.text), gnt=json.loads(gnt.text), images=images)






    
	


    	

      